here is an update that perhaps could be pasted for the test:

func TestUnbiased(t *testing.T) {
        if testing.Short() {
                t.Skip("Potentially long running test")
        }
        for max := int64(1 << 24); max < int64(1<<32); max = max << 1 {
                foundUnbiased := false
                for i := 0; i < 100; i++ {
                        if foundUnbiased && false {
                                break
                        }
                        t.Run(fmt.Sprintf("i=%d", i), func(t *testing.T) {
                                crd := &countingReader{r: rand.Reader}
                                ri, err := rand.Int(crd, big.NewInt(max))
                                if err != nil {
                                        t.Fatalf("rand.Int: max: %d error: %v", max, err)
                                }
                                if ri.Bytes()[0] != 0 {
                                        foundUnbiased = true
                                        return
                                }
                        })
                }
                if !foundUnbiased {
                        t.Fatalf("max: %d returned biased output with leading 0 through 100 samples", max)
                }
        }
}
